media_type.script:
var req = new CurlHttpRequest();
req.AddHeader('Content-Type: multipart/form-data');

Zabbix.Log(4, 'webhook request value=' + value);

params = JSON.parse(value);

resp = req.Post('https://dev.notify.events/api/v1/channel/source/' + params.token + '/execute',
  'subject=' + encodeURIComponent(params.subject) + '&message=' + encodeURIComponent(params.message),
);

Zabbix.Log(4, 'response code: ' + req.Status());

if (req.Status() != 200) {
    throw 'Response code: ' + req.Status();
}

return resp;
